,{"inputs":[],"name":"noticePeriod","outputs":[{"internalType":"uint32","name":"","type":"uint32"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"noticeTimeoutAt","outputs":[{"internalType":"uint64","name":"","type":"uint64"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"owner","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"pokeWeights","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"pool","outputs":[{"internalType":"contract IBPool","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"renounceOwnership","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"newManager","type":"address"}],"name":"setManager","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"bool","name":"value","type":"bool"}],"name":"setPublicSwap","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"newSwapFee","type":"uint256"}],"name":"setSwapFee","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"token","type":"address"},{"internalType":"uint256","name":"amount","type":"uint256"}],"name":"sweep","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"token0","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[],"name":"token1","outputs":[{"internalType":"address","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"newOwner","type":"address"}],"name":"transferOwnership","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"targetWeight0","type":"uint256"},{"internalType":"uint256","name":"targetWeight1","type":"uint256"},{"internalType":"uint256","name":"startBlock","type":"uint256"},{"internalType":"uint256","name":"endBlock","type":"uint256"}],"name":"updateWeightsGradually","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"validator","outputs":[{"internalType":"contract IWithdrawalValidator","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"amount0","type":"uint256"},{"internalType":"uint256","name":"amount1","type":"uint256"}],"name":"withdraw","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{"deposit(uint256,uint256)":{"params":{"amount0":"Amount to deposit of first token.","amount1":"Amount to deposit of second token."}},"getDenormalizedWeight(address)":{"returns":{"_0":"Denormalized weight value from underlying Balancer pool."}},"getSwapFee()":{"returns":{"_0":"Swap fee from underlying Balancer pool."}},"getWeightsChangeRatio(uint256,uint256)":{"details":"Will only be called by updateWeightsGradually().","params":{"targetWeight0":"Target weight of first token.","targetWeight1":"Target weight of second token."},"returns":{"_0":"Change ratio from current weights to target weights."}},"holdings0()":{"returns":{"_0":"Token balance in underlying pool"}},"holdings1()":{"returns":{"_0":"Token balance in underlying pool"}},"initialDeposit(uint256,uint256,uint256,uint256)":{"details":"Initial deposit must be performed before      calling withdraw() or deposit() functions.","params":{"amount0":"Deposited amount of first token.","amount1":"Deposited amount of second token.","weight0":"Initial weight of first token.","weight1":"Initial weight of second token."}},"isPublicSwap()":{"returns":{"_0":"If public swap is turned on, returns true, otherwise false."}},"owner()":{"details":"Returns the address of the current owner."},"renounceOwnership()":{"details":"Leaves the contract without owner. It will not