dateWeightsGradually","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[],"name":"validator","outputs":[{"internalType":"contract IWithdrawalValidator","name":"","type":"address"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256[]","name":"amounts","type":"uint256[]"}],"name":"withdraw","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"details":"Vault owner is the asset owner.","events":{"Created(address,address[],uint256[],address,address,uint32,string)":{"params":{"description":"Vault description.","factory":"Address of Balancer Managed Pool factory.","manager":"Address of vault manager.","noticePeriod":"Notice period in seconds.","tokens":"Address of tokens.","validator":"Address of withdrawal validator contract","weights":"Weights of tokens."}},"Deposit(uint256[],uint256[])":{"params":{"amounts":"Amounts of tokens.","weights":"Weights of tokens after deposit."}},"FinalizationInitialized(uint64)":{"params":{"noticeTimeoutAt":"Timestamp for notice timeout."}},"Finalized(address,uint256[])":{"params":{"amounts":"Returned amount of tokens.","caller":"Address of finalizer."}},"ManagerChanged(address,address)":{"params":{"manager":"Address of a new manager.","previousManager":"Address of previous manager."}},"SetSwapEnabled(bool)":{"params":{"swapEnabled":"New state of swap."}},"SetSwapFee(uint256)":{"params":{"swapFee":"New swap fee."}},"UpdateWeightsGradually(uint256,uint256,uint256[])":{"params":{"endTime":"End timestamp of updates.","startTime":"Start timestamp of updates.","weights":"The target weights of the tokens."}},"Withdraw(uint256[],uint256[],uint256[],uint256[])":{"params":{"allowances":"Allowance of tokens.","requestedAmounts":"Requested amount of tokens.","weights":"Weight of tokens after withdrawal.","withdrawnAmounts":"Withdrawn amount of tokens."}}},"kind":"dev","methods":{"constructor":{"details":"First token and second token shouldn't be same. Validator should conform 